mapper.select:
SELECT COUNT(1)
FROM tb_sys_auth

    AND auth_cd = #{authCd} 
    AND use_yn = #{useYn} 
    AND auth_nm LIKE CONCAT('%',#{authNm},'%')

mapper.update:
UPDATE tb_sys_auth SET
       auth_cd              = #{authCd},
       auth_nm              = #{authNm},
       auth_desc            = #{authDesc},
       mod_user_no          = #{modUserNo} 
WHERE auth_seq=#{authSeq}
