mapper.select:
SELECT A.menu_seq AS "menuSeq", B.auth_cd AS "authCd", A.company_cd AS "companyCd", (SELECT company_nm FROM tb_company WHERE company_cd=A.company_cd) AS "companyNm", A.menu_id AS "menuId", A.menu_nm AS "menuNm", A.menu_levl AS "menuLevl", A.menu_url AS "menuUrl", A.parent_menu_id AS "parentMenuId", A.menu_yn AS "menuYn" FROM tb_sys_menu A LEFT OUTER JOIN tb_sys_auth_menu B ON A.menu_seq = B.menu_seq and B.auth_cd = #{authCd}
AND A.company_cd = #{companyCd}
ORDER BY A.menu_id

mapper.update:
DELETE FROM tb_sys_auth_menu
 WHERE 1=1
   AND auth_cd=#{authCd}
