mapper.select:
SELECT company_seq AS "companySeq", company_cd AS "companyCd", company_nm AS "companyNm", company_nm_en AS "companyNmEn", use_start_dt AS "useStartDt", use_end_dt AS "useEndDt", company_logo_path AS "companyLogoPath", company_logo_file_nm AS "companyLogoFileNm", use_yn AS "useYn", reg_user_no AS "regUserNo", fn_getUserNm(reg_user_no) AS "regUserNm", reg_dt AS "regDt", mod_user_no AS "modUserNo", fn_getUserNm(mod_user_no) AS "modUserNm", date_format(mod_dt, '%Y-%m-%d %H:%i:%s') AS "modDt" FROM tb_company
AND company_cd LIKE CONCAT('%',#{companyCd},'%') AND company_nm LIKE CONCAT('%',#{companyNm},'%') AND company_nm_en LIKE CONCAT('%',#{companyNmEn},'%') AND use_start_dt >= #{useStartDtFr} AND use_start_dt < ADDDATE(#{useStartDtTo},1) AND use_start_dt >= #{useEndDtFr} AND use_end_dt < ADDDATE(#{useEndDtTo},1) AND use_yn = #{useYn} AND company_cd = #{companyCdForCheck}
ORDER BY reg_dt DESC
LIMIT #{offset}, #{length}

SELECT COUNT(1)
FROM tb_company

    AND company_cd LIKE CONCAT('%',#{companyCd},'%') 
    AND company_nm LIKE CONCAT('%',#{companyNm},'%') 
    AND company_nm_en LIKE CONCAT('%',#{companyNmEn},'%') 
    AND use_start_dt >= #{useStartDtFr} 
    AND use_start_dt < ADDDATE(#{useStartDtTo},1) 
    AND use_start_dt >= #{useEndDtFr} 
    AND use_end_dt < ADDDATE(#{useEndDtTo},1) 
    AND use_yn = #{useYn} 
    AND company_cd = #{companyCdForCheck}
