mapper.select:
SELECT menu_seq AS "menuSeq", company_cd AS "companyCd", (SELECT company_nm FROM tb_company WHERE company_cd=A.company_cd) AS "companyNm", menu_id AS "menuId", menu_nm AS "menuNm", menu_levl AS "menuLevl", menu_url AS "menuUrl", menu_desc AS "menuDesc", parent_menu_id AS "parentMenuId", menu_ordr AS "menuOrdr", use_yn AS "useYn", menu_yn AS "menuYn", reg_user_no AS "regUserNo", fn_getUserNm(reg_user_no) AS "regUserNm", date_format(reg_dt, '%Y-%m-%d %H:%i:%s') AS "regDt", mod_user_no AS "modUserNo", fn_getUserNm(mod_user_no) AS "modUserNm", date_format(mod_dt, '%Y-%m-%d %H:%i:%s') AS "modDt" FROM tb_sys_menu A
AND company_cd = #{companyCd} AND menu_id = #{menuId} AND menu_nm LIKE CONCAT('%',#{menuNm},'%') AND menu_url = #{menuUrl}
ORDER BY menu_seq DESC
LIMIT #{offset}, #{length}

SELECT COUNT(1)
FROM tb_sys_menu

    AND company_cd = #{companyCd} 
    AND menu_id = #{menuId} 
    AND menu_nm LIKE CONCAT('%',#{menuNm},'%') 
    AND menu_url = #{menuUrl}

SELECT DISTINCT A.menu_seq AS "menuSeq", A.company_cd AS "companyCd", A.menu_id AS "menuId", A.menu_nm AS "menuNm", A.menu_levl AS "menuLevl", A.menu_url AS "menuUrl", A.parent_menu_id AS "parentMenuId", A.menu_ordr AS "menuOrdr" FROM tb_sys_menu A INNER JOIN tb_sys_auth_menu B ON A.menu_seq = B.menu_seq WHERE A.use_yn = 'Y' AND A.menu_yn = #{menuYn} AND A.company_cd = #{companyCd}
and B.auth_cd IN
#{item}
AND A.menu_url = #{menuUrl} ORDER BY A.menu_ordr
