mapper.select:
SELECT COUNT(1)
  FROM tb_voc_mst A left outer join tb_voc_cust B on A.cust_seq = B.cust_seq
 WHERE 1=1
    AND A.del_yn = 'N' 
    AND A.company_cd = #{companyCd} 
    AND A.voc_id = #{vocId} 
    AND B.cust_nm LIKE CONCAT('%',#{custNm},'%') 
    AND A.voc_case_cd = #{vocCaseCd} 
    AND A.voc_type_cd1 = #{vocTypeCd1} 
    AND A.voc_type_cd2 = #{vocTypeCd2} 
    AND A.voc_type_cd3 = #{vocTypeCd3} 
    AND A.rcpt_chnn_cd = #{rcptChnnCd} 
    AND A.source_cd = #{sourceCd} 
    AND fn_getUserNm(A.reg_user_no) LIKE CONCAT('%',#{regUserNm},'%') 
    AND A.voc_title LIKE CONCAT('%',#{vocTitle},'%') 
    AND A.voc_act_type_cd1 = #{vocActTypeCd1} 
    AND A.voc_act_type_cd2 = #{vocActTypeCd2} 
    AND fn_getUserNm(A.voc_act_user_no) LIKE CONCAT('%',#{vocActUserNm},'%') 
    AND A.voc_status_cd = #{vocStatusCd} 
    AND A.reg_dt >= #{regDtStart} 
    AND A.reg_dt < ADDDATE(#{regDtEnd},1) 
    AND A.voc_act_dt >= #{vocActDtStart} 
    AND A.voc_act_dt < ADDDATE(#{vocActDtEnd},1) 
    AND A.cust_seq = #{custSeq}
    AND A.sens_spec_yn = #{sensSpecYn}

mapper.update:
UPDATE tb_voc_mst
SET voc_status_cd        = #{vocStatusCd},
    mod_user_no          = #{modUserNo}
WHERE voc_seq = #{vocSeq}

UPDATE tb_voc_mst
SET del_yn = 'Y',
    mod_user_no          = #{modUserNo}
WHERE voc_seq = #{vocSeq}
