mapper.select:
SELECT COUNT(1)
  FROM tb_sys_user
 WHERE use_yn = 'Y'
    AND company_cd = #{companyCd} 
    AND user_id LIKE CONCAT('%',#{userId},'%') 
    AND user_id = #{userIdForCheck} 
    AND user_nm LIKE CONCAT('%',#{userNm},'%') 
    AND tel_no LIKE CONCAT('%',#{telNo},'%') 
    AND email_addr LIKE CONCAT('%',#{emailAddr},'%') 
    AND employment_yn = #{employmentYn}

mapper.update:
UPDATE tb_sys_user
   SET use_yn = 'N'
 WHERE user_seq=#{userSeq}
