mapper.select:
SELECT A.cust_seq AS "custSeq", A.company_cd AS "companyCd", (SELECT company_nm FROM tb_company WHERE company_cd=A.company_cd) AS "companyNm", A.cust_no AS "custNo", A.cust_nm AS "custNm", A.cust_nm_en AS "custNmEn", A.tel_no AS "telNo", A.email_addr AS "emailAddr", A.del_yn AS "delYn", A.reg_user_no AS "regUserNo", fn_getUserNm(A.reg_user_no) AS "regUserNm", date_format(A.reg_dt, '%Y-%m-%d %H:%i:%s') AS "regDt", A.mod_user_no AS "modUserNo", fn_getUserNm(A.mod_user_no) AS "modUserNm", date_format(A.mod_dt, '%Y-%m-%d %H:%i:%s') AS "modDt", (SELECT COUNT(1) FROM tb_voc_mst WHERE cust_seq = A.cust_seq AND del_yn = 'N') AS "vocCnt", (SELECT COUNT(1) FROM tb_voc_mst WHERE cust_seq = A.cust_seq AND sens_spec_yn = 'Y' AND del_yn = 'N') AS "sensVocCnt" FROM tb_voc_cust A WHERE A.del_yn = 'N' AND A.cust_no != CONCAT(A.company_cd,'_99999') AND A.acust_seq = #{custSeq} AND A.company_cd = #{companyCd} AND A.cust_no = #{custNo} AND A.cust_nm LIKE CONCAT('%',#{custNm},'%') AND A.cust_nm_en LIKE CONCAT('%',#{custNmEn},'%') AND A.tel_no = #{telNo} AND A.reg_dt >= #{regDtStart} AND A.reg_dt < ADDDATE(#{regDtEnd},1)
AND (SELECT COUNT(1) FROM tb_voc_mst WHERE cust_seq = A.cust_seq AND del_yn = 'N') >= #{vocCnt}
ORDER BY A.cust_seq DESC
LIMIT #{offset}, #{length}

SELECT COUNT(1)
  FROM tb_voc_cust A
 WHERE A.del_yn = 'N'
    AND A.cust_no != CONCAT(A.company_cd,'_99999') 
    AND cust_seq = #{custSeq} 
    AND company_cd = #{companyCd} 
    AND cust_no = #{custNo} 
    AND cust_nm LIKE CONCAT('%',#{custNm},'%') 
    AND cust_nm_en LIKE CONCAT('%',#{custNmEn},'%') 
    AND tel_no = #{telNo} 
    AND A.reg_dt >= #{regDtStart} 
    AND A.reg_dt < ADDDATE(#{regDtEnd},1) 

        AND (SELECT COUNT(1) FROM tb_voc_mst WHERE cust_seq = A.cust_seq AND del_yn = 'N') >= #{vocCnt}

mapper.update:
UPDATE tb_voc_cust
   SET del_yn = 'Y'
 WHERE cust_seq=#{custSeq}
